mapper.select:
select *
from (select a.id        as id,
             a.word_name as wordName,
             a.ph_am     as phAm,
             a.ph_an_mp3 as mp3,
             b.user,
             a.acceptation
      from word a,
           word_user_rel b
      where a.id = b.word_id
     ) data ${ew.customSqlSegment}
