mapper.select:
        SELECT n.*
		   FROM  note_info n
		   WHERE n.create_by = #{createBy,jdbcType=VARCHAR}

               and n.parent_id = #{parentId,jdbcType=VARCHAR}

		   and n.del_flag = 0
		   order by n.update_time DESC

SELECT n.id,n.name,n.parent_id,n.parent_ids,n.tag FROM note_info n WHERE n.create_by = #{createBy,jdbcType=VARCHAR}
and n.parent_ids like CONCAT('%','${parentId}','%' )
and n.del_flag = 0 order by n.update_time DESC
